import './App.css';
import React from 'react'
import {useState} from 'react'
/* import Box from '@mui/material/Box'; */
/* import Button from '@mui/material/Button'; */
/*import TextField from '@material-ui/core/TextField';*/
/*import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';*/
/*<TextField id="standardtextfield" label="Enter qty" variant="standard" />*/
/*
<form noValidate autoComplete = "off">
        <TextField
        label = "Enter qty"
        />
      </form>
*/

/* Implemented at least 2 components other than the Projects component in HardwareSet and Button*/

/* Reused Selection component multiple times within the Projects component to further modularize the project*/
function HardwareSet(props){
  const setNum = useState(0);
  return(
    <header>
      <form className = 'customheader'> HW Set {props.number}: {props.initsize}/{props.initsize}
        <label> 
        <input type = "number" min = {0} max = {props.initsize} placeholder = {props.title} onChange={e=> setNum(e.target.value)}/>
        </label>
        <button className = 'in'>Check In</button>
        <button className = 'out'>Check Out</button>
      </form>
    </header>
  )
}

/*
function HardwareSet(props){
  //Store Capacity
  //Store Availability
  //Get Value From TextField Input
  //Make sure it is integer
  //Else, default to 0
  //Subtract inputted value from availability
  //If more than availability, make availability 0
  //If adding more than capacity, make availability capacity
  //Do not change after that (no need to issue statements)
}
*/

/* Reused Button component multiple times within the Projects component to further modularize the project*/
/* Used component Button to have custom event handler to change name of button from Join to Leave */
function Button(){
  const [btnName, setName] = useState(false);
  return (
    <button onClick={() => setName(!btnName)} style = {{backgroundColor:  'orange'}}>
      {`${btnName ? 'Leave' : 'Join'}`}
    </button>
  );  
}

/* Passed props from parent to child twice (App passed props to Projects and Projects passed props to HardwareSet)*/
/* Also passed props from parent to child again when Projects passed props to Button */
function Projects(props) {
  const [userName, setUser] = useState('User');
  const changeUser = event => {
    setUser(event.target.value);
  };
  return (
    <header> 
      <input type = 'text' value = {userName} onChange = {changeUser}/>
      <h1>Hello {userName}! This is the project catalog:</h1>
      <h3 classname = 'newheader'>Project {props.number/3} <Button/></h3>
      <h3>List of Authorized Users: </h3>
      <h2><HardwareSet number = {props.number/3} initsize = {props.initsize} title = {props.title}/></h2>
      <h2><HardwareSet number = {props.number*2/3} initsize = {props.initsize} title = {props.title}/></h2>
      <h3 classname = 'newheader'>Project {props.number*2/3} <Button/></h3>
      <h3>List of Authorized Users: </h3>
      <h2><HardwareSet number = {props.number*3/3} initsize = {props.initsize} title = {props.title}/></h2>
      <h2><HardwareSet number = {props.number*4/3} initsize = {props.initsize} title = {props.title}/></h2>
      <h3 classname = 'newheader'>Project {props.number*3/3} <Button/></h3>
      <h3>List of Authorized Users: </h3>
      <h2><HardwareSet number = {props.number*5/3} initsize = {props.initsize} title = {props.title}/></h2>
      <h2><HardwareSet number = {props.number*6/3} initsize = {props.initsize} title = {props.title}/></h2>
    </header>
  )
}

/* Passed props from parent to child twice (App passed props to Projects and Projects passed props to HardwareSet)*/
class App extends React.Component {
  render(){
    return (
      <div className="App">
        <header className="App-header">
          <Projects title = 'Enter Qty' number = {3} initsize = {100}/>
        </header>
      </div>
    );
  }
}

export default App;
